#!/bin/bash
# Change SSH Port to 12345
perl -pi -e 's/^#?Port 22$/Port 12345/' /etc/ssh/sshd_config
service sshd restart || service ssh restart

# Add ssh keys for root user (/root/.ssh/authorized_keys)
echo "ssh-rsa  orlando@hashlabs.com" >> /root/.ssh/authorized_keys
echo "ssh-rsa " >> /root/.ssh/authorized_keys
echo "ssh-rsa  pateketrueke@gmail.com" >> /root/.ssh/authorized_keys
echo "ssh-rsa  pateketrueke@gmail.com" >> /root/.ssh/authorized_keys

# Add ssh keys for ubuntu user (/home/ubuntu/.ssh/authorized_keys)
mkdir -p /home/ubuntu/.ssh
echo "ssh-rsa  orlando@hashlabs.com" >> /home/ubuntu/.ssh/authorized_keys
echo "ssh-rsa " >> /home/ubuntu/.ssh/authorized_keys
echo "ssh-rsa  pateketrueke@gmail.com" >> /home/ubuntu/.ssh/authorized_keys
echo "ssh-rsa  pateketrueke@gmail.com" >> /home/ubuntu/.ssh/authorized_keys

# Know Hosts
ssh-keyscan -H 'gitlab.com' >> $HOME/.ssh/known_hosts
ssh-keyscan -H 'github.com' >> $HOME/.ssh/known_hosts

# EBS Device
sudo mkfs -t ext4 /dev/xvdg
sudo mkdir -p /data
sudo mount /dev/xvdg /data
sudo echo "/dev/xvdg       /data   ext4    defaults,nofail        0       2" >> /etc/fstab
sudo mount -a
chmod 777 /data
